SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_receber_trib_update ON titulo_receber_trib CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_receber_trib_update() RETURNS trigger AS $BODY$
DECLARE
nr_interno_conta_w	bigint;
nr_seq_protocolo_w	bigint;
ie_digitar_imposto_w	varchar(1) := 'N';


BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
select	coalesce(max(nr_interno_conta),0),
	coalesce(max(nr_seq_protocolo),0)
into STRICT	nr_interno_conta_w,
	nr_seq_protocolo_w
from	titulo_receber
where	nr_titulo	= NEW.NR_TITULO;

ie_digitar_imposto_w := Obter_Param_Usuario(801, 93, wheb_usuario_pck.get_cd_perfil, NEW.nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_digitar_imposto_w);

if	((coalesce(NEW.ie_origem_tributo,'C')  = 'D') and
	((nr_interno_conta_w > 0) or (nr_seq_protocolo_w > 0)) and (ie_digitar_imposto_w = 'N')) and (wheb_usuario_pck.get_cd_funcao <> 135) then
	-- Este tributo nao pode ser digitado pois o titulo e originado de uma conta ou um protocolo!

	CALL wheb_mensagem_pck.exibir_mensagem_abort(267077);
end if;

end if;

RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_receber_trib_update() FROM PUBLIC;

CREATE TRIGGER titulo_receber_trib_update
	BEFORE INSERT OR UPDATE ON titulo_receber_trib FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_receber_trib_update();

