if (coalesce(wheb_usuario_pck.get_ie_lote_contabil,'N') = 'N') and (coalesce(wheb_usuario_pck.get_ie_atualizacao_contabil,'N') = 'N') then
	if (OLD.nr_titulo_externo is null) and (NEW.nr_titulo_externo is not null) then
		NEW.nr_titulo_ext_numerico	:= somente_numero(NEW.nr_titulo_externo);

	elsif (OLD.nr_titulo_externo <> NEW.nr_titulo_externo) then
		NEW.nr_titulo_ext_numerico	:= somente_numero(NEW.nr_titulo_externo);
	end if;
end if;
end if;
RETURN NEW;
end;
