DROP TRIGGER IF EXISTS tlc_agenda_pep ON agenda_consulta CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_tlc_agenda_pep() RETURNS trigger AS $BODY$
declare

pragma autonomous_transaction;
BEGIN
  BEGIN

if (TG_OP = 'DELETE') then
	delete from pep_tlc_registros where nr_seq_agenda = OLD.nr_sequencia;
	commit;
elsif (TG_OP = 'UPDATE') and (NEW.dt_atendido is not null) and (OLD.dt_atendido is null) then
	CALL gravar_consulta_telemedicina(NEW.nr_atendimento,NEW.nr_sequencia,'F','', NEW.nm_usuario);
end if;

exception when others then
	CALL gravar_log_tasy(20894,
					SUBSTR('Erro ao realizar processo tlc no pep = ' || substr(sqlerrm,2000),1,2000),
					wheb_usuario_pck.get_nm_usuario);
  END;
IF TG_OP = 'DELETE' THEN
	RETURN OLD;
ELSE
	RETURN NEW;
END IF;

end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_tlc_agenda_pep() FROM PUBLIC;

CREATE TRIGGER tlc_agenda_pep
	AFTER UPDATE OR DELETE ON agenda_consulta FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_tlc_agenda_pep();

