DROP TRIGGER IF EXISTS transf_protuario_bef_inst_upd ON transf_prontuario CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_transf_protuario_bef_inst_upd() RETURNS trigger AS $BODY$
DECLARE
    cd_pessoa_fisica_w varchar(10);
BEGIN
    IF ( NEW.nr_atendimento IS NOT NULL ) THEN
        SELECT
            a.cd_pessoa_fisica
        INTO STRICT cd_pessoa_fisica_w
        FROM
            atendimento_paciente a
        WHERE
            a.nr_atendimento = NEW.nr_atendimento;

        IF ( cd_pessoa_fisica_w <> NEW.cd_pessoa_fisica ) THEN
            CALL wheb_mensagem_pck.exibir_mensagem_abort(1136006);
        END IF;

    END IF;
RETURN NEW;
END
$BODY$
