CREATE OR REPLACE FUNCTION trigger_fct_trans_financ_contab_insert() RETURNS trigger AS $BODY$
declare
ds_erro_w		varchar(255);
ie_tipo_w		conta_contabil.ie_tipo%type;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
if (NEW.cd_conta_contabil is not null) then
	BEGIN
	ie_tipo_w	:= substr(obter_dados_conta_contabil(NEW.cd_conta_contabil,null,'T'),1,1);
	if (ie_tipo_w = 'T') then
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(43666, 'CTA=' || NEW.CD_CONTA_CONTABIL);
	end if;
	end;
end if;

if (NEW.cd_conta_contabil is not null) and (NEW.cd_centro_custo is not null) then
	ds_erro_w := consiste_grupo_centro_conta(NEW.cd_conta_contabil, NEW.cd_centro_custo, null, ds_erro_w);
	if (ds_erro_w is not null) then
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(279953, 'DS_ERRO=' || ds_erro_w);
	end if;
end if;

ds_erro_w := con_consiste_vigencia_conta(NEW.cd_conta_contabil, NEW.dt_inicio_vigencia, NEW.dt_fim_vigencia, ds_erro_w);
if (ds_erro_w is not null) then
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(279953, 'DS_ERRO=' || ds_erro_w);
end if;

end if;

RETURN NEW;
END;
$BODY$
