SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS trans_solicitacao_bfinsert ON trans_solicitacao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_trans_solicitacao_bfinsert() RETURNS trigger AS $BODY$
DECLARE

nr_sequencia_w	bigint;
nr_idade_pf_w	bigint;
BEGIN
  BEGIN

/* Gravar histórico de status da solicitação de transporte */

select	nextval('trans_historico_seq')
into STRICT	nr_sequencia_w
;

insert into trans_historico(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_solicitacao,
	ie_status,
	dt_inicio,
	dt_final)
values (nr_sequencia_w,
	LOCALTIMESTAMP,
	NEW.nm_usuario,
	LOCALTIMESTAMP,
	NEW.nm_usuario,
	NEW.nr_sequencia,
	NEW.ie_status,
	LOCALTIMESTAMP,
	null);

/*  Tratar se a solicitação se encaixa em alguma regra de prioridade */

nr_idade_pf_w := Obter_Idade_PF(NEW.cd_pessoa_fisica, LOCALTIMESTAMP, 'A');

BEGIN
	select	nr_sequencia
	into STRICT	nr_sequencia_w
	from	trans_regra_prioridade
	where	cd_setor_atendimento = NEW.cd_setor_origem
	and	nr_idade_pf_w < qt_menor_faixa
	and	nr_idade_pf_w > qt_maior_faixa;
exception
	when	no_data_found then
		nr_sequencia_w := 0;
end;

If (nr_sequencia_w > 0) then
	NEW.ie_regra_prioridade := 'S';
else
	NEW.ie_regra_prioridade := 'N';
end if;

  END;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_trans_solicitacao_bfinsert() FROM PUBLIC;

CREATE TRIGGER trans_solicitacao_bfinsert
	BEFORE INSERT ON trans_solicitacao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_trans_solicitacao_bfinsert();

