CREATE OR REPLACE FUNCTION trigger_fct_tre_evento_atual() RETURNS trigger AS $BODY$
declare

BEGIN
if (NEW.dt_fim_real is not null) then
	update	tre_agenda
	set	dt_termino_real	= NEW.dt_fim_real
