SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS tx_doador_potencial_insert ON tx_doador_potencial CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_tx_doador_potencial_insert() RETURNS trigger AS $BODY$
declare
ie_tipo_orgao_receptor_w	varchar(15);
ie_tipo_orgao_doador_w	varchar(15);
 
BEGIN 
 
/* Verifica compatibilidade entre órgãos */
 
select	substr(tx_obter_dados_receptor(NEW.nr_seq_receptor, 'TO'),1,15) 
into STRICT	ie_tipo_orgao_receptor_w
;
 
select	tx_obter_dados_orgao(nr_seq_orgao, 'T') 
into STRICT	ie_tipo_orgao_doador_w 
from	tx_doador_orgao 
where	nr_sequencia	= NEW.nr_seq_doador_orgao;
 
if (ie_tipo_orgao_receptor_w <> ie_tipo_orgao_doador_w) then 
	--Os órgãos do doador e receptor são incompatíveis. 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264223);
	 
end if;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_tx_doador_potencial_insert() FROM PUBLIC;

CREATE TRIGGER tx_doador_potencial_insert
	BEFORE INSERT ON tx_doador_potencial FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_tx_doador_potencial_insert();

