SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS tx_receptor_prioridade_insert ON tx_receptor_prioridade CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_tx_receptor_prioridade_insert() RETURNS trigger AS $BODY$
declare
 
cd_pessoa_fisica_w		varchar(10);
nm_pessoa_fisica_w		varchar(255);
cd_perfil_w			integer;
BEGIN
  BEGIN 
 
 
BEGIN 
select	cd_perfil_prior 
into STRICT	cd_perfil_w 
from	tx_parametros;
exception 
	when others then 
	cd_perfil_w	:= 0;
end;
 
if (cd_perfil_w > 0) then 
 
	select	cd_pessoa_fisica, 
		substr(obter_nome_pf(cd_pessoa_fisica),1,80) 
	into STRICT	cd_pessoa_fisica_w, 
		nm_pessoa_fisica_w 
	from	tx_receptor b 
	where	NEW.nr_seq_receptor = b.nr_sequencia 
	and	tx_obter_se_paciente_ativo(b.nr_sequencia) = 'S';
 
	if (cd_pessoa_fisica_w is not null) then 
		insert into comunic_interna( 
			dt_comunicado, 
			ds_titulo, 
			ds_comunicado, 
			nm_usuario, 
			dt_atualizacao, 
			ie_geral, 
			nm_usuario_destino, 
			ds_perfil_adicional, 
			nr_sequencia, 
			ie_gerencial, 
			dt_liberacao, 
			cd_estab_destino 
		) values ( 
			LOCALTIMESTAMP, 
			wheb_mensagem_pck.get_texto(798733) || ' ', 
			wheb_mensagem_pck.get_texto(798762, 
						'CD_PESSOA_FISICA='||cd_pessoa_fisica_w|| 
						';NM_PESSOA_FISICA='||nm_pessoa_fisica_w), 
			NEW.nm_usuario, 
			LOCALTIMESTAMP, 
			'N', 
			'', 
			cd_perfil_w||', ', 
			nextval('comunic_interna_seq'), 
			'N', 
			LOCALTIMESTAMP, 
			null 
		);
	end if;
end if;
 
  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_tx_receptor_prioridade_insert() FROM PUBLIC;

CREATE TRIGGER tx_receptor_prioridade_insert
	BEFORE INSERT ON tx_receptor_prioridade FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_tx_receptor_prioridade_insert();

