DROP TRIGGER IF EXISTS unitarizacao_atual ON unitarizacao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_unitarizacao_atual() RETURNS trigger AS $BODY$
declare
ie_manipulacao_w	varchar(1);
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;
BEGIN
if (NEW.cd_pessoa_manipulacao is not null) then
	select	coalesce(min(ie_manipulacao),'N')
	into STRICT	ie_manipulacao_w
	from	sup_pessoa_unitarizacao
	where	cd_estabelecimento = cd_estabelecimento_w
	and	cd_pessoa_fisica = NEW.cd_pessoa_manipulacao
	and	ie_situacao = 'A';

	if (ie_manipulacao_w = 'N') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266345);
		--'Pessoa física não permitida para manipulação!');
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_unitarizacao_atual() FROM PUBLIC;

CREATE TRIGGER unitarizacao_atual
	BEFORE INSERT OR UPDATE ON unitarizacao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_unitarizacao_atual();

