SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS update_hospitalization_detail ON gestao_vaga CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_update_hospitalization_detail() RETURNS trigger AS $BODY$
declare

qt_registro_w  bigint;

BEGIN

if (OLD.nr_atendimento is null AND NEW.nr_atendimento is not null) and (coalesce(wheb_usuario_pck.get_ie_executar_trigger, 'S') = 'S') then

  select count(nr_sequencia)
  into STRICT  qt_registro_w
  from  atendimento_paciente_inf
  where nr_atendimento = NEW.nr_atendimento;

  if (qt_registro_w > 0) then

    update atendimento_paciente_inf
      set 
      cd_hospitalization_route = NEW.cd_hospitalization_route, 
      cd_pre_hosp_home = NEW.cd_pre_hosp_home,
      si_cons_med_guid =NEW.si_cons_med_guid
    where nr_atendimento = NEW.nr_atendimento;

  else

    insert into atendimento_paciente_inf(
      nr_sequencia,
      dt_atualizacao,
      nm_usuario, 
      nr_atendimento, 
      cd_hospitalization_route, 
      cd_pre_hosp_home,
      si_cons_med_guid)
    values (
      nextval('atendimento_paciente_inf_seq'),
      LOCALTIMESTAMP, 
      wheb_usuario_pck.get_nm_usuario, 
      NEW.nr_atendimento,
      NEW.cd_hospitalization_route, 
      NEW.cd_pre_hosp_home,
      NEW.si_cons_med_guid);

  end if;

end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_update_hospitalization_detail() FROM PUBLIC;

CREATE TRIGGER update_hospitalization_detail
	AFTER UPDATE ON gestao_vaga FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_update_hospitalization_detail();

