DROP TRIGGER IF EXISTS usuario_atividade_atual ON usuario_atividade CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_usuario_atividade_atual() RETURNS trigger AS $BODY$
DECLARE
nm_user_w		varchar(50);
dt_entrada_w		timestamp;

BEGIN

select	username
into STRICT	nm_user_w
from	user_users;

if (nm_user_w = 'CORP') then
	BEGIN
	select	dt_entrada
	into STRICT	dt_entrada_w
	from	usuario_controle
	where	nr_sequencia	= NEW.NR_SEQ_CONTROLE;

	if (trunc(dt_entrada_w,'dd') <> trunc(NEW.dt_atividade,'dd')) then
		--A data da atividade deve ser igual a data do controle.#@#@');
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(261545);
	end if;

	end;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_usuario_atividade_atual() FROM PUBLIC;

CREATE TRIGGER usuario_atividade_atual
	BEFORE INSERT OR UPDATE ON usuario_atividade FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_usuario_atividade_atual();

