SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS valida_duplic_classif_bancario ON classif_bancario CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_valida_duplic_classif_bancario() RETURNS trigger AS $BODY$
DECLARE
subtype                    nr_seq_reg_size is smallint;
ie_reg_w	                 nr_seq_reg_size;

pragma autonomous_transaction;
BEGIN

IF (wheb_usuario_pck.get_ie_executar_trigger = 'S') THEN

  SELECT
      CASE WHEN EXISTS(
          SELECT a.NR_SEQUENCIA
          FROM   CLASSIF_BANCARIO a
          WHERE  a.nr_sequencia <> NEW.nr_sequencia
          AND    a.CD_CLASSIFICACAO = NEW.CD_CLASSIFICACAO
          AND    ((a.CD_ENTIDADE_CLASSIFICADORA = 4 AND a.DS_OUTRA_ENTIDADE = NEW.DS_OUTRA_ENTIDADE)
          OR (a.CD_ENTIDADE_CLASSIFICADORA <> 4 AND a.CD_ENTIDADE_CLASSIFICADORA = NEW.CD_ENTIDADE_CLASSIFICADORA)))
      THEN 1 ELSE 0
      END
  INTO STRICT ie_reg_w;

  IF (ie_reg_w = 1) THEN
    CALL wheb_mensagem_pck.exibir_mensagem_abort(1196403, 'DS_CLASSIFICACAO='||NEW.CD_CLASSIFICACAO);
  END IF;

END IF;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_valida_duplic_classif_bancario() FROM PUBLIC;

CREATE TRIGGER valida_duplic_classif_bancario
	BEFORE INSERT OR UPDATE ON classif_bancario FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_valida_duplic_classif_bancario();

