SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS wl_regra_worklist_atual ON wl_regra_worklist CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_wl_regra_worklist_atual() RETURNS trigger AS $BODY$
declare
ie_regra_worklist_w		char(1);

BEGIN

select	(CASE WHEN max(nr_sequencia) IS NOT NULL THEN 'S' ELSE 'N' END)
into STRICT	ie_regra_worklist_w
from	wl_regra_worklist
where	nr_seq_item = NEW.nr_seq_item
and		nr_seq_item = (	SELECT	nr_sequencia
						from	wl_item
						where	nr_sequencia = nr_seq_item
						and		ie_situacao = 'A');

if (ie_regra_worklist_w = 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1026121);
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_wl_regra_worklist_atual() FROM PUBLIC;

CREATE TRIGGER wl_regra_worklist_atual
	BEFORE INSERT ON wl_regra_worklist FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_wl_regra_worklist_atual();

