DROP TRIGGER IF EXISTS w_sip_item_despesa_delete ON w_sip_item_despesa CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_w_sip_item_despesa_delete() RETURNS trigger AS $BODY$
declare

dt_envio_w	timestamp;

BEGIN

select	coalesce(max(dt_envio),'')
into STRICT	dt_envio_w
from	pls_lote_sip
where	nr_sequencia	= OLD.nr_seq_lote_sip;

if (dt_envio_w is not null) then
	-- O lote SIP já foi enviado para a ANS, não é permitido a exclusão de registros desse período!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(267079);
end if;

RETURN OLD;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
