			and (NEW.IE_TIPO_CODIGO_EXTERNO is null or ie_tipo_codigo_externo   = NEW.IE_TIPO_CODIGO_EXTERNO);
		end if;
	
		if (qt_w > 0) then
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(1026386);				
		end if;		
	end if;
end if;
RETURN NEW;
end;
