	substr(obter_desc_topografia_dor(b.nr_seq_topografia),1,60) ds_topografia,
	'S' ie_assoc_adep,
	c.nr_sequencia nr_seq_horario,
	c.dt_horario,
	c.dt_fim_horario dt_adm,
	1 qt_adm,
	'' cd_um,
	1 qt_dose,
	'' cd_unidade_medida_dose,
	'' ie_via,
	d.nr_sequencia nr_seq_evento,
	d.ie_alteracao ie_evento,
	d.dt_alteracao dt_evento,
	d.cd_pessoa_fisica cd_pessoa_evento,
	substr(obter_nome_pf(d.cd_pessoa_fisica),1,60) nm_pessoa_evento,
	coalesce(d.ds_observacao,d.ds_justificativa) ds_evento,
	'' ds_horarios,
	null nm_usuario_adm,
	substr(b.ds_observacao, 1,255) ds_observacao_sae,
	null ds_obs_prescr,
	d.ds_justificativa,
	b.cd_intervalo,
	null qt_total_dispensar	,
	null cd_unidade_disp,
	'N' ie_medicacao_paciente,
  (null)::numeric  nr_agrupamento,
    0 nr_seq_cpoe
from	pessoa_fisica p,
	prescr_mat_alteracao d,
	pe_prescr_proc_hor c,
	pe_prescr_proc b,
	pe_prescricao a
where	d.nr_seq_horario_sae = c.nr_sequencia
and	c.nr_seq_pe_proc = b.nr_sequencia
and	c.nr_seq_pe_prescr = b.nr_seq_prescr
and	b.nr_seq_prescr = a.nr_sequencia
and	a.cd_prescritor	= p.cd_pessoa_fisica
and	c.dt_fim_horario is not null
and	coalesce(c.ie_situacao,'A') = 'A'
and	coalesce(b.ie_adep,'N') in ('S','M')
--and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S';		// OS 332013 - Linha substituida pela linha abaixo

and	a.dt_liberacao is not null

union all

select	8 nr_seq_apresent,
	'LD' ie_tipo_item,
	/*'Leite e derivados'*/
 obter_desc_expressao(304869) ds_tipo_item,
	/*'Leite e derivados'*/
 obter_desc_expressao(304869) ds_tipo_item_plural,
	'N' ie_laboratorio,
	a.nr_atendimento,
	a.nr_prescricao,
	a.cd_prescritor,
	substr(p.nm_pessoa_fisica,1,60) nm_prescritor,
	'' ds_horarios_adm,
	'' ds_prescricao,
	a.dt_prescricao,
	a.dt_inicio_prescr,
	a.dt_validade_prescr,
	a.dt_liberacao,
	b.nr_sequencia nr_seq_item,
	CASE WHEN coalesce(b.ie_se_necessario,'N')='S' THEN  'S'  ELSE CASE WHEN padroniza_horario(b.ds_horarios)='' THEN  'S'  ELSE 'N' END  END  ie_acm_sn,
	'N' ie_di,
	to_char(c.cd_material) cd_item,
	substr(obter_desc_material(c.cd_material),1,200)||CASE WHEN b.ie_lado IS NULL THEN ''  ELSE ' Lado: '||obter_valor_dominio(1372,b.ie_lado) END  ds_item,
	'' ds_diluente,
	'' ds_topografia,
	'N' ie_assoc_adep,
	c.nr_sequencia nr_seq_horario,
	c.dt_horario,
	c.dt_fim_horario dt_adm,
	d.qt_dose_adm qt_adm,
	'' cd_um,
	e.qt_volume_total qt_dose,
	'' cd_unidade_medida_dose,
	'' ie_via,
	d.nr_sequencia nr_seq_evento,
	d.ie_alteracao ie_evento,
	d.dt_alteracao dt_evento,
	d.cd_pessoa_fisica cd_pessoa_evento,
	substr(obter_nome_pf(d.cd_pessoa_fisica),1,60) nm_pessoa_evento,
	coalesce(d.ds_observacao,d.ds_justificativa) ds_evento,
	'' ds_horarios,
	null nm_usuario_adm,
	substr(b.ds_observacao, 1,255) ds_observacao_sae,
	null ds_obs_prescr,
	d.ds_justificativa,
	b.cd_intervalo,
	null qt_total_dispensar,
	null cd_unidade_disp,
	'N' ie_medicacao_paciente,
	(null)::numeric  nr_agrupamento,
    b.nr_seq_mat_cpoe nr_seq_cpoe
from	pessoa_fisica p,
        prescr_leite_deriv e,
        prescr_mat_alteracao d,
        prescr_mat_hor c,
        prescr_material b,
        prescr_medica a
where	b.nr_prescricao = c.nr_prescricao
and	b.nr_prescricao = a.nr_prescricao
and	c.nr_prescricao = a.nr_prescricao
and	e.nr_prescricao	= b.nr_prescricao
and	e.nr_prescricao	= d.nr_prescricao
and e.nr_sequencia	= b.nr_seq_leite_deriv
and	b.nr_sequencia = c.nr_seq_material	
and	b.cd_material = c.cd_material
and d.nr_seq_horario = c.nr_sequencia
and	a.cd_prescritor	= p.cd_pessoa_fisica
and	c.dt_fim_horario is not null
AND c.dt_suspensao IS NULL
and	coalesce(c.ie_situacao,'A') = 'A'
and	coalesce(a.ie_adep,'S') = 'S'
and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S';

