CREATE OR REPLACE VIEW adep_hist_v (nr_seq_apresent, ie_tipo_item, ds_tipo_item, ds_tipo_item_plural, nr_atendimento, nr_prescricao, dt_prescricao, dt_inicio_prescr, dt_validade_prescr, dt_liberacao, cd_prescritor, nm_prescritor, nr_seq_item, cd_item, ds_item, nr_seq_horario, dt_horario) AS select	1 nr_seq_apresent,
