CREATE OR REPLACE VIEW adep_item_prescr_v (nr_seq_apresent, ie_tipo_item, ds_tipo_item, ds_tipo_item_plural, nr_atendimento, nr_prescricao, dt_validade_prescr, dt_liberacao, nr_seq_item, cd_item, ds_item, ie_via, cd_um, qt_dose, ie_acm, ie_sn, cd_intervalo, ds_dose_diferenciada, ie_tipo) AS select	1 nr_seq_apresent,
	'D' ie_tipo_item, 
	'Dieta oral' ds_tipo_item, 
	'Dietas orais' ds_tipo_item_plural, 
	a.nr_atendimento nr_atendimento, 
	a.nr_prescricao nr_prescricao, 
	a.dt_validade_prescr dt_validade_prescr, 
	a.dt_liberacao dt_liberacao, 
	--null nr_seq_item, 
	0 nr_seq_item, 
	c.cd_refeicao cd_item, 
	substr(obter_valor_dominio(99,c.cd_refeicao),1,240) ds_item, 
	'' ie_via, 
	'' cd_um, 
	1 qt_dose, 
	'N' ie_acm, 
	'N' ie_sn, 
	'' cd_intervalo, 
	'' ds_dose_diferenciada, 
	'M' ie_tipo 
FROM	prescr_dieta_hor c, 
	prescr_medica a 
where	c.nr_prescricao = a.nr_prescricao 
and	coalesce(a.ie_adep,'S') = 'S' 
and	a.dt_liberacao is not null 
and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S' 

