	CASE WHEN obter_se_ctrl_glic_proc(nr_seq_proc_interno)='S' THEN  'G'  ELSE CASE WHEN b.nr_seq_exame IS NULL THEN  'P'  ELSE 'L' END  END  ie_tipo_item, 
