	to_char(b.cd_procedimento) cd_item, 
	substr(obter_desc_prescr_proc(b.cd_procedimento, b.ie_origem_proced, b.nr_seq_proc_interno),1,240) ds_item, 
	'' ie_via, 
	'' cd_um, 
	b.qt_procedimento qt_dose, 
	coalesce(b.ie_acm,'N') ie_acm, 
