FROM prescr_recomendacao b, prescr_medica a, prescr_rec_hor c
LEFT OUTER JOIN prescr_mat_alteracao d ON (c.nr_sequencia = d.nr_seq_horario_rec)
WHERE c.nr_seq_recomendacao = b.nr_sequencia and c.nr_prescricao = b.nr_prescricao and b.nr_prescricao = a.nr_prescricao and c.dt_suspensao is not null and coalesce(c.ie_situacao,'A') = 'A' and coalesce(a.ie_adep,'S') = 'S' and Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
 
union

select	7 nr_seq_apresent,
	'E' ie_tipo_item,
	obter_desc_expressao(929193) ds_tipo_item,
	obter_desc_expressao(929193) ds_tipo_item_plural,
	'N' ie_laboratorio,
	a.nr_atendimento,
	a.nr_sequencia,
	a.cd_prescritor,
	substr(obter_nome_pf(a.cd_prescritor),1,60) nm_prescritor,
	a.dt_prescricao,
	a.dt_inicio_prescr,
	a.dt_validade_prescr,
	a.dt_liberacao,
	b.nr_sequencia nr_seq_item,
	CASE WHEN coalesce(b.ie_se_necessario,'N')='S' THEN  'S'  ELSE CASE WHEN padroniza_horario(b.ds_horarios)='' THEN  'S'  ELSE 'N' END  END  ie_acm_sn,
	'N' ie_di,
	to_char(b.nr_seq_proc) cd_item,
	substr(obter_desc_intervencoes(b.nr_seq_proc),1,240) ds_item,
	null ds_diluente,
	substr(obter_desc_topografia_dor(b.nr_seq_topografia),1,60) ds_topografia,
	'S' ie_assoc_adep,
	c.nr_sequencia nr_seq_horario,
	c.dt_horario,
	c.dt_suspensao dt_susp,
	1 qt_susp,
	'' cd_um,
	1 qt_dose,
	'' cd_unidade_medida_dose,
	'' ie_via,
	d.nr_sequencia nr_seq_evento,
	d.ie_alteracao ie_evento,
	d.dt_alteracao dt_evento,
	d.cd_pessoa_fisica cd_pessoa_evento,
	substr(obter_nome_pf(d.cd_pessoa_fisica),1,60) nm_pessoa_evento,
	coalesce(d.ds_observacao,d.ds_justificativa) ds_evento,
	d.ds_justificativa,
	'N' ie_medicacao_paciente,
  (null)::numeric  nr_agrupamento,
    0 nr_seq_cpoe
FROM pe_prescr_proc b, pe_prescricao a, pe_prescr_proc_hor c
LEFT OUTER JOIN prescr_mat_alteracao d ON (c.nr_sequencia = d.nr_seq_horario_sae)
WHERE c.nr_seq_pe_proc = b.nr_sequencia and c.nr_seq_pe_prescr = b.nr_seq_prescr and b.nr_seq_prescr = a.nr_sequencia and c.dt_suspensao is not null and coalesce(c.ie_situacao,'A') = 'A' --and	nvl(b.ie_adep,'N') = 'S'
  and coalesce(b.ie_adep,'N') in ('S','M')
 
union

select	8 nr_seq_apresent,
	'LD' ie_tipo_item,
	/*'Leite e derivados'*/
 obter_desc_expressao(304869) ds_tipo_item,
	/*'Leite e derivados'*/
 obter_desc_expressao(304869) ds_tipo_item_plural,
	'N' ie_laboratorio,
	a.nr_atendimento,
	a.nr_prescricao,
	a.cd_prescritor,
	substr(obter_nome_pf(a.cd_prescritor),1,60) nm_prescritor,
	a.dt_prescricao,
	a.dt_inicio_prescr,
	a.dt_validade_prescr,
	a.dt_liberacao,
	b.nr_sequencia nr_seq_item,
	CASE WHEN coalesce(b.ie_acm,'N')='S' THEN  'S'  ELSE CASE WHEN coalesce(b.ie_se_necessario,'N')='S' THEN  'S'  ELSE 'N' END  END  ie_acm_sn,
	coalesce(b.ie_bomba_infusao,'N') ie_di,
	to_char(b.cd_material) cd_item,
	substr(obter_desc_material(b.cd_material),1,240) ds_item,
	substr(obter_diluicao_medic_adep(c.nr_sequencia),1,240) ds_diluente,
	null ds_topografia,
	'N' ie_assoc_adep,
	c.nr_sequencia nr_seq_horario,
	c.dt_horario,
	c.dt_suspensao dt_susp,
	c.qt_dispensar_hor qt_susp,
	c.cd_unidade_medida cd_um,
	c.qt_dose,
	c.cd_unidade_medida_dose,
	b.ie_via_aplicacao ie_via,
	d.nr_sequencia nr_seq_evento,
	d.ie_alteracao ie_evento,
	d.dt_alteracao dt_evento,
	d.cd_pessoa_fisica cd_pessoa_evento,
	substr(obter_nome_pf(d.cd_pessoa_fisica),1,60) nm_pessoa_evento,
	coalesce(d.ds_observacao,d.ds_justificativa) ds_evento,
	d.ds_justificativa,
	b.ie_medicacao_paciente,
    b.nr_agrupamento nr_agrupamento,
    b.nr_seq_mat_cpoe nr_seq_cpoe
from	prescr_leite_deriv e,
        prescr_mat_alteracao d,
		prescr_mat_hor c,
		prescr_material b,
		prescr_medica a
where	c.nr_seq_material = b.nr_sequencia
and	c.nr_prescricao = b.nr_prescricao
and	b.nr_prescricao = a.nr_prescricao
and	e.nr_prescricao	= b.nr_prescricao
and	d.nr_prescricao	= b.nr_prescricao
and e.nr_sequencia	= b.nr_seq_leite_deriv
and	c.dt_suspensao is not null
and	coalesce(c.ie_situacao,'A') = 'A'
and	coalesce(c.ie_adep,'S') = 'S'
and	b.ie_agrupador = 16
and	coalesce(a.ie_adep,'S') = 'S'
and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
and	obter_se_gerar_item_adep('M',b.nr_prescricao,b.nr_sequencia,b.nr_agrupamento,'N') = 'S';

