SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW agendas_localizador_v (cd_agenda, ds_agenda) AS select cd_agenda,
	   CASE WHEN cd_tipo_Agenda=3 THEN  substr(obter_nome_medico_combo_agcons(cd_estabelecimento, cd_agenda, cd_tipo_agenda, coalesce(ie_ordenacao,'N')),1,255)  ELSE ds_agenda END  ds_agenda
  FROM agenda 
 where ie_situacao = 'A' 
   and coalesce(ie_agenda_integrada,'N') = 'S';

