CREATE OR REPLACE VIEW agenda_quimio_v (dt_referencia, nr_seq_local, ds_local, cd_setor_atendimento, ds_setor_atendimento, cd_estabelecimento, ds_estabelecimento) AS select	trunc(a.dt_agenda) dt_referencia,
	a.nr_seq_local,
	b.ds_local,
	b.cd_setor_atendimento,
	substr(obter_nome_setor(b.cd_setor_atendimento),1,100) ds_setor_atendimento,
	b.cd_estabelecimento,
	substr(obter_nome_estabelecimento(b.cd_estabelecimento),1,255) ds_estabelecimento
FROM	agenda_quimio a,
	qt_local b
where	a.nr_seq_local = b.nr_sequencia
and	a.ie_status_agenda not in ('C','F','S')
and	a.nr_seq_atendimento is not null;

