SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW atributos_regra_alteracao_oc_v (nm_tabela, nm_atributo) AS select	nm_tabela,
	nm_atributo
FROM	tabela_atributo
where	nm_tabela = 'ORDEM_COMPRA'
and	nm_atributo in ('IE_EMITE_OBS','IE_URGENTE','CD_COMPRADOR','VL_FRETE','IE_FRETE','CD_LOCAL_ENTREGA','IE_AVISO_CHEGADA','NR_SEQ_SUBGRUPO_COMPRA','NR_SEQ_FORMA_PAGTO','DS_OBSERVACAO','DT_VALIDADE')

union all

select	nm_tabela,
	nm_atributo
from	tabela_atributo
where	nm_tabela = 'ORDEM_COMPRA_ITEM'
and	nm_atributo in ('CD_LOCAL_ESTOQUE','CD_CENTRO_CUSTO','NR_SEQ_CONTA_FINANC','NR_SEQ_MARCA','NR_SEQ_CRITERIO_RATEIO','PR_DESCONTOS','VL_DESCONTO')
order by 1,2;

