SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW autocomplete_agendamento_pep_v (cd, ds) AS select cd_agenda cd,
	CASE WHEN cd_tipo_Agenda=3 THEN  substr(obter_nome_medico_combo_agcons(cd_estabelecimento, cd_agenda, cd_tipo_agenda, coalesce(ie_ordenacao,'N')),1,255)  ELSE ds_agenda END  ds 
	FROM agenda 
	where ie_situacao = 'A' 
	and	cd_tipo_agenda not in (1,2,5) 
	and	coalesce(ie_tipo_agenda_consulta,'A') <> 'T' 
	order by ds;

