CREATE OR REPLACE VIEW autorizacao_mat_disp_v (nr_atendimento, nr_seq_autorizacao, cd_material, qt_autorizada, qt_utilizada) AS select	/*+ index (c  MATPACI_AUTCONV_FK_I) */           	a.nr_atendimento,
           	a.nr_seq_autorizacao,
           	a.cd_material,
           	a.qt_autorizada,
           	coalesce(b.qt_material,0) qt_utilizada
FROM material_autorizado a
LEFT OUTER JOIN material_atend_paciente b ON (a.nr_atendimento = b.nr_atendimento AND a.nr_seq_autorizacao = b.nr_seq_autorizacao AND a.cd_material = b.cd_material);

