CREATE OR REPLACE VIEW autorizacao_proc_disp_v (nr_atendimento, nr_seq_autorizacao, cd_procedimento, qt_autorizada, qt_utilizada, ie_origem_proced) AS select	/*+ index (c  PROPACI_AUTCONV_FK_I) */           	a.nr_atendimento,
           	a.nr_seq_autorizacao,
           	a.cd_procedimento,
           	a.qt_autorizada,
           	coalesce(b.qt_procedimento,0) qt_utilizada,
		a.ie_origem_proced
FROM procedimento_autorizado a
LEFT OUTER JOIN procedimento_paciente b ON (a.nr_atendimento = b.nr_atendimento AND a.nr_seq_autorizacao = b.nr_seq_autorizacao AND a.cd_procedimento = b.cd_procedimento);

