CREATE OR REPLACE VIEW bft_insurance_auth_proc_v (procedure_authorization_id, cd_procedimento, procedure_code, authorization_id, illness_code) AS select     a.nr_sequencia procedure_authorization_id,
           a.CD_PROC_PRINCIPAL_LOC cd_procedimento,
           CASE WHEN a.ie_origem_proced=20 THEN a.CD_PROC_PRINCIPAL_LOC  ELSE null END  procedure_code,
           a.nr_sequencia authorization_id,
           CASE WHEN a.ie_origem_proced=21 THEN a.CD_PROC_PRINCIPAL_LOC  ELSE null END  illness_code
FROM       autorizacao_convenio a;

