CREATE OR REPLACE VIEW cep_compl_pf_mx_v (ds_bairro, cd_cep, cd_municipio_ibge, ds_uf, cd_tipo_asen, nr_seq_localidade, nr_seq_pais) AS select	distinct b.cd_assentamento ds_bairro,
	a.codigo cd_cep,
	d.cd_municipio_ibge  cd_municipio_ibge,
	a.cve_ent ds_uf,
	b.cd_tipo_asen cd_tipo_asen,
	a.cve_loc nr_seq_localidade,
	c.nr_sequencia nr_seq_pais
FROM	cat_codigo_postal a,
	cat_assentamento b,
	pais c,
	sus_municipio d
where 	1=1
and    c.nr_seq_cat_pais = 142
and   	d.nr_seq_entidade_mx  = a.cve_ent
and   	d.nr_seq_municipio_mx = a.cve_mun
and   b.cd_entidade = a.cve_ent
and	  b.cd_municipio = a.cve_mun
and   b.cd_localidade = a.cve_loc
and   b.cd_assentamento = a.cve_asen;

