CREATE OR REPLACE VIEW cih_infeccao_v (nr_sequencia, nr_ficha_ocorrencia, cd_clinica, cd_topografia, nr_seq_classif_top, nr_ih, nr_ic, nr_sem, nr_outras, dt_infeccao, cd_setor_atendimento, cd_caso_infeccao) AS select  b.nr_sequencia,
	b.nr_ficha_ocorrencia,
	b.cd_clinica,
	b.cd_topografia,
	b.nr_seq_classif_top,
	CASE WHEN coalesce(c.ie_tipo_caso, b.cd_caso_infeccao)=1 THEN 1  ELSE 0 END  nr_ih,
	CASE WHEN coalesce(c.ie_tipo_caso, b.cd_caso_infeccao)=2 THEN 1  ELSE 0 END  nr_ic,
	CASE WHEN coalesce(c.ie_tipo_caso, b.cd_caso_infeccao)=3 THEN 1  ELSE 0 END  nr_sem,
	CASE WHEN coalesce(c.ie_tipo_caso, b.cd_caso_infeccao)=1 THEN 0 WHEN coalesce(c.ie_tipo_caso, b.cd_caso_infeccao)=2 THEN 0 WHEN coalesce(c.ie_tipo_caso, b.cd_caso_infeccao)=3 THEN 0  ELSE 1 END  nr_outras,
	b.dt_infeccao,
	b.cd_setor_atendimento,
	b.cd_caso_infeccao
FROM 	cih_caso_infeccao c,
	cih_local_infeccao b,
	cih_ficha_ocorrencia a
where	a.nr_ficha_ocorrencia	= b.nr_ficha_ocorrencia
and	b.cd_caso_infeccao	= c.cd_caso_infeccao

union

select  0,
	a.nr_ficha_ocorrencia,
	0,
	0,
	0,
	0,
	0,
	count(*),
	0,
	null,
	null,
	null
from 	cih_ficha_ocorrencia a
where	not exists (select 1 from cih_local_infeccao b
		    where b.nr_ficha_ocorrencia = a.nr_ficha_ocorrencia)
group by a.nr_ficha_ocorrencia;

