CREATE OR REPLACE VIEW cm_conj_pend_consiste_v (nr_seq_lote_transf, nr_seq_conjunto_cont, nr_seq_item_cont, nr_seq_conjunto, ie_transf_receb) AS select	a.nr_seq_lote_transf,
	a.nr_seq_conjunto nr_seq_conjunto_cont,
	b.nr_sequencia nr_seq_item_cont,
	b.nr_seq_conjunto,
	'T' ie_transf_receb
FROM	cm_lote_transf_conj a,
	cm_conjunto_cont b,
	cm_item_cont c
where	a.nr_seq_conjunto = b.nr_sequencia
and	b.nr_sequencia = c.nr_seq_conjunto
and	coalesce(a.ie_consistido_transf,'N') <> 'S'
and	not exists (select	1
			from	cm_transf_conj_cb x
			where	x.nr_seq_lote_transf = a.nr_seq_lote_transf
			and	x.nr_seq_item = c.nr_sequencia
			and	ie_transf_receb = 'T')

union all

select	a.nr_seq_lote_transf,
	a.nr_seq_conjunto nr_seq_conjunto_cont,
	b.nr_sequencia nr_seq_item_cont,
	b.nr_seq_conjunto,
	'R' ie_transf_receb
from	cm_lote_transf_conj a,
	cm_conjunto_cont b,
	cm_item_cont c
where	a.nr_seq_conjunto = b.nr_sequencia
and	b.nr_sequencia = c.nr_seq_conjunto
and	coalesce(a.ie_consistido_receb,'N') <> 'S'
and	not exists (select	1
			from	cm_transf_conj_cb x
			where	x.nr_seq_lote_transf = a.nr_seq_lote_transf
			and	x.nr_seq_item = c.nr_sequencia
			and	ie_transf_receb = 'R');

