SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW cm_item_apelido_v (nr_sequencia, nm_item, ds_apelido) AS select	a.nr_sequencia,
	a.nm_item,
	null ds_apelido
FROM 	cm_item a
where	a.ie_situacao = 'A'
  and   not exists (select 1
	from cm_item_apelido b
	where a.nr_sequencia = b.nr_seq_item)

union

select	a.nr_sequencia,
	a.nm_item,
	b.ds_apelido
from	cm_item a,
	cm_item_apelido b
where	a.ie_situacao  = 'A'
  and	a.nr_sequencia = b.nr_seq_item;

