SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW cobranca_escrit_caixa_150_v (tp_registro, nr_seq_envio, cd_banco, cd_cgc, cd_convenio, cd_agencia_bancaria, nm_empresa, cd_movimento, dt_vencimento, dt_arquivo, vl_cobranca, vl_tot_deb, vl_tot_cre, qt_reg_arquivo, nm_banco, nr_conta, ie_digito_conta, cd_operacao, cd_cartao) AS select	1 tp_registro,
	a.nr_sequencia nr_seq_envio, 
	a.cd_banco, 
	b.cd_cgc, 
	c.cd_convenio_banco cd_convenio, 
	c.cd_agencia_bancaria, 
	substr(obter_razao_social(b.cd_cgc),1,100) nm_empresa, 
	' ' cd_movimento, 
	LOCALTIMESTAMP dt_vencimento, 
	LOCALTIMESTAMP dt_arquivo, 
	0 vl_cobranca, 
	0 vl_tot_deb, 
	0 vl_tot_cre, 
	0 qt_reg_arquivo, 
	substr(obter_nome_banco(a.cd_banco),1,100) nm_banco, 
	' ' nr_conta, 
	' ' ie_digito_conta, 
	'00' cd_operacao, 
	'' cd_cartao 
FROM	estabelecimento b, 
	banco_estabelecimento c, 
	cobranca_escritural a 
where	a.cd_estabelecimento	= b.cd_estabelecimento 
and	a.nr_seq_conta_banco	= c.nr_sequencia 

union
 
SELECT	2 tp_registro, 
	a.nr_sequencia nr_seq_envio, 
	a.cd_banco, 
	' ' cd_cgc, 
	' ' cd_convenio, 
	SUBSTR(c.cd_agencia_bancaria,1,4) cd_agencia_bancaria, 
	' ' nm_empresa, 
	'00' cd_movimento, 
	b.dt_pagamento_previsto dt_vencimento, 
	LOCALTIMESTAMP	dt_arquivo, 
	c.vl_cobranca, 
	0 vl_tot_deb, 
	0 vl_tot_cre, 
	0 qt_reg_arquivo, 
	SUBSTR(obter_nome_banco(a.cd_banco),1,100) nm_banco, 
	c.nr_conta nr_conta, 
	c.ie_digito_conta ie_digito_conta, 
	'00' cd_operacao, 
	SUBSTR(b.CD_PESSOA_FISICA || '-'||pls_obter_carteira_segurado(y.nr_sequencia),1,30) cd_cartao 
FROM titulo_receber_cobr c, cobranca_escritural a, titulo_receber_v b
LEFT OUTER JOIN pls_mensalidade_segurado x ON (b.nr_seq_mens_segurado = x.nr_sequencia)
LEFT OUTER JOIN pls_segurado y ON (x.nr_seq_segurado = y.nr_sequencia)
WHERE a.nr_sequencia		= c.nr_seq_cobranca AND c.nr_titulo		= b.nr_titulo   
union
 
select	3 tp_registro, 
	a.nr_sequencia nr_seq_envio, 
	a.cd_banco, 
	' ' cd_cgc, 
	' ' cd_convenio, 
	' ' cd_agencia_bancaria, 
	' ' nm_empresa, 
	' ' cd_movimento, 
	LOCALTIMESTAMP dt_vencimento, 
	LOCALTIMESTAMP dt_arquivo, 
	0 vl_cobranca, 
	sum(b.vl_cobranca) vl_tot_deb, 
	0 vl_tot_cre, 
	(count(*)+2) qt_reg_arquivo, 
	' ' nm_banco, 
	' ' nr_conta, 
	' ' ie_digito_conta, 
	'00' cd_operacao, 
	'' cd_cartao 
from	titulo_receber_cobr b, 
	cobranca_escritural a 
where	a.nr_sequencia		= b.nr_seq_cobranca 
group by a.nr_sequencia, 
 	 a.cd_banco;

