	sum(obter_saldo_disp_estoque(a.cd_estabelecimento, b.cd_material_estoque, null, trunc(LOCALTIMESTAMP,'month'))),
	b.cd_unidade_medida_estoque,
	11,
	a.cd_estabelecimento
from 	material b,
	Parametro_estoque a
group by
