	0,
	trunc(LOCALTIMESTAMP,'month'),
	sum(sup_obter_saldo_estoque(a.cd_estabelecimento, trunc(LOCALTIMESTAMP,'month'), null, b.cd_material_estoque)),
	b.cd_unidade_medida_estoque,
	12,
