SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW consiste_banco_v (nm_objeto, object_type) AS select SUBSTR(object_name,1,50) NM_OBJETO , object_type
FROM USER_OBJECTS
where object_type in ('PROCEDURE', 'VIEW', 'SEQUENCE', 'TRIGGER', 'PACKAGE')
and not exists (select nm_objeto
   from objeto_sistema
   where object_name = upper(nm_objeto))

union

select SUBSTR(object_name,1,50) NM_OBJETO ,  'Table'
from USER_OBJECTS
where object_type = 'TABLE'
and not exists (select upper(nm_tabela)
   from Tabela_sistema
   where object_name = nm_tabela)

union

select SUBSTR(object_name,1,50) NM_OBJETO ,  'Index'
from USER_OBJECTS
where object_type = 'INDEX'
and not exists (select nm_indice
   from Indice
   where object_name = nm_indice)

union

select constraint_name, 'Constraint'
from user_constraints
where constraint_type = 'R'
    and not exists (select nm_integridade_referencial
           from integridade_referencial
           where constraint_name = nm_integridade_referencial)

union

select constraint_name, 'PK'
from user_constraints
where constraint_type = 'P'
    and not exists (select nm_indice
           from indice
           where table_name = nm_tabela
                and constraint_name = nm_indice)

union

 select a.table_name, a.column_name
 from user_tab_columns a, tab b
 where a.table_name = b.tname
  and b.tabtype = 'TABLE'
  and not exists (select nm_tabela
  from tabela_atributo
  where a.table_name = nm_tabela
  and a.column_name = nm_atributo);

