CREATE OR REPLACE VIEW consiste_tabela_v (ds_obs, nm_tabela) AS SELECT 'Falta Base ' ds_obs,
    a.nm_tabela
 FROM tabela_sistema a
 where not exists (select b.table_name
    from user_tables b
    where a.nm_tabela = b.table_name)

union

