SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW conta_paciente_anterior_v (nr_interno_conta, nr_interno_conta_ant, dt_periodo_inicial, dt_periodo_final, ie_emite_conta) AS select	a.nr_interno_conta,
	b.nr_interno_conta nr_interno_conta_ant,
	b.dt_periodo_inicial,
	b.dt_periodo_final,
	24 ie_emite_conta
FROM	protocolo_convenio c,
	conta_paciente b,
	conta_paciente a
where	a.nr_interno_conta	<> b.nr_interno_conta
and	a.nr_atendimento	= b.nr_atendimento
and	b.nr_seq_protocolo	= c.nr_seq_protocolo
and	b.dt_periodo_inicial	< a.dt_periodo_final
and (c.dt_envio		is not null or
	c.dt_entrega_convenio	is not null)
and	b.ie_cancelamento 	is null
and	c.ie_status_protocolo	= 2
and	a.cd_convenio_parametro	= b.cd_convenio_parametro
and	a.cd_estabelecimento	= b.cd_estabelecimento;

