CREATE OR REPLACE VIEW conta_paciente_tot_matmed_v (nr_interno_conta, ie_emite_conta, vl_material, vl_medicamento, vl_matmed) AS select	a.nr_interno_conta,
	'59' ie_emite_conta,
	sum(CASE WHEN ie_tipo_material=2 THEN  0 WHEN ie_tipo_material=3 THEN  0  ELSE vl_material END ) vl_material,
	sum(CASE WHEN ie_tipo_material=2 THEN  vl_material WHEN ie_tipo_material=3 THEN  vl_material  ELSE 0 END ) vl_medicamento,
	sum(vl_material) vl_matmed
FROM	material b,
   	material_atend_paciente a
where	a.cd_material = b.cd_material
and	a.cd_motivo_exc_conta is null
group by nr_interno_conta;

