CREATE OR REPLACE VIEW conta_procedimento_v (cd_area_proced, cd_especial_proced, cd_grupo_proced, cd_procedimento, ie_origem_proced, cd_conta_receita, cd_setor_atendimento, ie_tipo_atendimento, ie_classif_convenio, ds_area_procedimento, ds_especialidade, ds_grupo_proc, ds_procedimento, ds_conta_contabil) AS Select 	coalesce(a.cd_area_proced,0) cd_area_proced,
	coalesce(a.cd_especial_proced,0) cd_especial_proced,
	coalesce(a.cd_grupo_proced,0) cd_grupo_proced,
	coalesce(a.cd_procedimento,0) cd_procedimento,
