	a.ie_classif_convenio,
	b.ds_area_procedimento,
	c.ds_especialidade,
	d.ds_grupo_proc,
	e.ds_procedimento,
	f.ds_conta_contabil
FROM conta_contabil f, area_procedimento b, parametros_conta_contabil a
LEFT OUTER JOIN especialidade_proc c ON (a.cd_especial_proced = c.cd_especialidade)
LEFT OUTER JOIN grupo_proc d ON (a.cd_grupo_proced = d.cd_grupo_proc)
