SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW controle_acomp_risco_v (nr_pendencia, nr_sequencia, ds_pendencia) AS select	1 nr_pendencia,
	a.nr_sequencia,
	substr(obter_valor_dominio(4959,1),1,255)ds_pendencia
FROM	proj_projeto a
where	exists	(select 1
		from	proj_risco_implantacao x
		where	x.nr_seq_proj = a.nr_sequencia
		and	((x.dt_fim_real is null)
		or (x.nr_seq_tipo is null)));

