SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW ctb_mes_planejamento_v (nr_sequencia, nr_seq_mes_ref, dt_referencia) AS select  a.nr_sequencia,
        b.nr_sequencia nr_seq_mes_ref,
        b.dt_referencia
FROM    ctb_mes_ref b,
        ctb_orc_cenario a
where   b.dt_referencia between ctb_obter_mes_ref(a.nr_seq_mes_inicio) and ctb_obter_mes_ref(a.nr_seq_mes_fim)
order by a.nr_sequencia, b.dt_referencia;

