CREATE OR REPLACE VIEW ctb_movimento_export_v (tp_registro, cd_estabelecimento, nr_seq_mes_ref, cd_tipo_lote_contabil, nr_lote_contabil, nr_sequencia, dt_movimento, cd_conta_debito, cd_conta_credito, vl_movimento, cd_historico, cd_centro_custo, ds_compl_historico, nr_seq_agrupamento) AS select	1 tp_registro,
	a.cd_estabelecimento,
	b.nr_seq_mes_ref,
	b.cd_tipo_lote_contabil,
	a.nr_lote_contabil,
	a.nr_sequencia,
