SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW cus_horas_manutencao_v (dt_referencia, cd_estabelecimento, cd_centro_controle, qt_distribuicao) AS select	trunc(a.dt_atividade,'mm') dt_referencia,
	c.cd_estabelecimento,
	d.cd_centro_custo cd_centro_controle,
	round((dividir(sum(a.qt_minuto),60))::numeric,4) qt_distribuicao
FROM	setor_atendimento d,
	man_localizacao c,
	man_ordem_servico b,
	man_ordem_serv_ativ a
where	a.nr_seq_ordem_serv	= b.nr_sequencia
and	b.nr_seq_localizacao	= c.nr_sequencia
and	c.cd_setor		= d.cd_setor_atendimento
group by	trunc(a.dt_atividade,'mm'),
		c.cd_estabelecimento,
		d.cd_centro_custo;

