SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW cus_ordem_servico_solic_v (dt_referencia, cd_estabelecimento, cd_centro_controle, qt_distribuicao) AS select	trunc(a.dt_ordem_servico,'mm') dt_referencia,
	b.cd_estabelecimento,
	c.cd_centro_custo cd_centro_controle,
	count(*) qt_distribuicao
FROM	setor_atendimento c,
	usuario b,
	man_ordem_servico a
where	a.cd_pessoa_solicitante	= b.cd_pessoa_fisica
and	b.cd_setor_atendimento	= c.cd_setor_atendimento
and	c.cd_centro_custo is not null
group by	trunc(a.dt_ordem_servico,'mm'),
		b.cd_estabelecimento,
		c.cd_centro_custo;

