SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW cus_requisicao_v (cd_estabelecimento, dt_referencia, cd_centro_controle, qt_distribuicao) AS select	a.cd_estabelecimento,
	trunc(dt_solicitacao_requisicao,'MM') dt_referencia,
	a.cd_centro_controle,
	count(*) qt_distribuicao
FROM	centro_controle a,
	setor_atendimento b,
	requisicao_material c
where	a.cd_centro_controle		= b.cd_centro_controle
and	a.cd_estabelecimento		= b.cd_estabelecimento
and	c.cd_estabelecimento		= b.cd_estabelecimento
and	c.cd_setor_atendimento	= b.cd_setor_atendimento
group by	a.cd_estabelecimento,
		trunc(dt_solicitacao_requisicao,'MM'),
		a.cd_centro_controle;

