CREATE OR REPLACE VIEW dar_advisory_v (charttime, encouterid, systemid, ptcensusid, interventionlonglabel) AS select  LOCALTIMESTAMP as chartTime,
        p.nr_atendimento as encouterId,
        p.cd_estabelecimento systemId,
        c.nr_sequencia as ptCensusId,
        obter_valor_dominio(17, p.ie_clinica) interventionLongLabel
FROM atendimento_paciente p
inner join dar_census c on c.nr_atendimento = p.nr_atendimento;

