SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW dashboard_pmo_proj_chkpoint_v (nr_sequencia, ds_titulo, qt_checkpoint_prev, qt_checkpoint_real, ie_status_checkpoint, qt_total_checkpoint) AS select x.NR_SEQUENCIA,x.DS_TITULO,x.QT_CHECKPOINT_PREV,x.QT_CHECKPOINT_REAL,x.IE_STATUS_CHECKPOINT,x.QT_TOTAL_CHECKPOINT
FROM   ( select t.nr_sequencia,
                t.ds_titulo,
				/*Total de checkpoint previsto para estar concluído até a data de hoje*/

                ( select   count(e.nr_sequencia) qt_checkpoint_prev
                  from     proj_projeto p,
                           proj_cronograma c,
                           proj_cron_etapa e
                  where    p.nr_sequencia  = c.nr_seq_proj
                  and      c.nr_sequencia  = e.nr_seq_cronograma
                  and      c.ie_situacao   = 'A'
                  and      e.ie_checkpoint = 'S'
                  and      p.nr_sequencia  =  t.nr_sequencia
				  and	   trunc(e.dt_fim_prev)	<= trunc(LOCALTIMESTAMP)
				  /*Total de checkpoint concluídos/realizados*/

                ) qt_checkpoint_prev,
                ( select   count(e.nr_sequencia) qt_checkpoint_real
                  from     proj_projeto p,
                           proj_cronograma c,
                           proj_cron_etapa e
                  where    p.nr_sequencia  = c.nr_seq_proj
                  and      c.nr_sequencia  = e.nr_seq_cronograma
                  and      c.ie_situacao   = 'A'
                  and      e.ie_checkpoint = 'S'
                  and      p.nr_sequencia  =  t.nr_sequencia
                  and      e.dt_fim_real is not null
                 ) qt_checkpoint_real,
                obter_status_ckp_dashboard_pmo('PJ', t.nr_sequencia) ie_status_checkpoint,
				/*Total de checkpoint do projeto*/

				( select   count(e.nr_sequencia) qt_total_checkpoint
                  from     proj_projeto p,
                           proj_cronograma c,
                           proj_cron_etapa e
                  where    p.nr_sequencia  = c.nr_seq_proj
                  and      c.nr_sequencia  = e.nr_seq_cronograma
                  and      c.ie_situacao   = 'A'
                  and      e.ie_checkpoint = 'S'
                  and      p.nr_sequencia =  t.nr_sequencia
                ) qt_total_checkpoint
        from   proj_projeto t
       ) x
where  x.qt_total_checkpoint > 0;

